public void onCreate(Bundle savedInstanceState) &#123;  super.onCreate(savedInstanceState);  if (savedInstanceState != null) &#123;    mWebView.restoreState(savedInstanceState);  &#125; else &#123;    mWebView.loadUrl("http://2tu.github.io/2016/05/14/android-data-store/");  &#125;&#125;
